// Element selections
const questionDisplay = document.getElementById('questionDisplay');
const showAnswerBtn = document.getElementById('showAnswerBtn');
const nextQuestionBtn = document.getElementById('nextQuestionBtn');
const chapterButtons = document.querySelectorAll('.chapter-btn');
const randomAllBtn = document.getElementById('randomAllBtn');
const darkModeToggle = document.getElementById('darkModeToggle');
const sidebar = document.getElementById('sidebar');
const mainToggleSidebarBtn = document.getElementById('mainToggleSidebar'); // Hamburger button in header
const mainContent = document.getElementById('mainContent');
const mainContainer = document.getElementById('mainContainer'); // Reference to the container

let currentQuestions = [];
let currentQuestionIndex = -1;
let isAnswerShown = false;

// Functions
function displayQuestion(questionObj) {
    questionDisplay.innerHTML = `<p class="arabic-text fade-in">${questionObj.q}</p>`;
    showAnswerBtn.style.display = 'block';
    nextQuestionBtn.style.display = 'none';
    isAnswerShown = false;
}

function displayAnswer(questionObj) {
    questionDisplay.innerHTML = `<p class="arabic-text">${questionObj.q}</p><p class="arabic-text fade-in answer-text">${questionObj.a}</p>`;
    showAnswerBtn.style.display = 'none';
    nextQuestionBtn.style.display = 'block';
    isAnswerShown = true;
}

function getNextQuestion(chapter) {
    let questionsToDrawFrom;
    if (chapter === 'all') {
        questionsToDrawFrom = Object.values(questions).flat();
    } else {
        questionsToDrawFrom = questions[chapter];
    }

    if (!questionsToDrawFrom || questionsToDrawFrom.length === 0) {
        questionDisplay.innerHTML = '<p class="arabic-text">بو بابده سوال بولونميور.</p>';
        showAnswerBtn.style.display = 'none';
        nextQuestionBtn.style.display = 'none';
        return;
    }

    currentQuestions = questionsToDrawFrom; // Set current context
    currentQuestionIndex = Math.floor(Math.random() * currentQuestions.length);
    displayQuestion(currentQuestions[currentQuestionIndex]);
}

// Event Listeners

// Toggle Sidebar Functionality
mainToggleSidebarBtn.addEventListener('click', () => {
    sidebar.classList.toggle('open');
    if (window.innerWidth <= 768) { // Only shift content on mobile
        mainContent.classList.toggle('shifted');
    }
});

// Close sidebar if clicking outside (on mobile)
document.addEventListener('click', (event) => {
    if (window.innerWidth <= 768) { // Apply only for mobile screen sizes
        const isClickInsideSidebar = sidebar.contains(event.target);
        const isClickOnToggleBtn = mainToggleSidebarBtn.contains(event.target);
        const isSidebarOpen = sidebar.classList.contains('open');

        // If sidebar is open AND click is outside sidebar AND not on the toggle button
        if (isSidebarOpen && !isClickInsideSidebar && !isClickOnToggleBtn) {
            sidebar.classList.remove('open');
            mainContent.classList.remove('shifted');
        }
    }
});


// Chapter Buttons
chapterButtons.forEach(button => {
    button.addEventListener('click', () => {
        const chapter = button.dataset.chapter;
        getNextQuestion(chapter);
        // Sidebar remains open as per new requirement
    });
});

// Random All Button
randomAllBtn.addEventListener('click', () => {
    getNextQuestion('all');
    // Sidebar remains open as per new requirement
});


showAnswerBtn.addEventListener('click', () => {
    if (currentQuestions[currentQuestionIndex]) {
        displayAnswer(currentQuestions[currentQuestionIndex]);
    }
});

nextQuestionBtn.addEventListener('click', () => {
    if (currentQuestions.length > 0) {
        // If current context is 'all', pick another random from all
        if (currentQuestions.length === Object.values(questions).flat().length) {
            getNextQuestion('all');
        } else { // Otherwise, pick another random from the current chapter
            const currentChapterKey = Object.keys(questions).find(key => questions[key] === currentQuestions);
            getNextQuestion(currentChapterKey);
        }
    } else {
        questionDisplay.innerHTML = '<p class="arabic-text">لطفاً، اوّلجه بیر باب سئچکز و یا بتون سواللر طوگمەسينى باصڭ.</p>';
        showAnswerBtn.style.display = 'none';
        nextQuestionBtn.style.display = 'none';
    }
});

darkModeToggle.addEventListener('click', () => {
    document.body.classList.toggle('dark-mode');
    // Save preference to localStorage
    if (document.body.classList.contains('dark-mode')) {
        localStorage.setItem('darkMode', 'enabled');
    } else {
        localStorage.setItem('darkMode', 'disabled');
    }
});

// Initial load: Check for dark mode preference and initial layout
document.addEventListener('DOMContentLoaded', () => {
    if (localStorage.getItem('darkMode') === 'enabled') {
        document.body.classList.add('dark-mode');
    }

    // Adjust initial layout based on screen size
    handleLayoutOnResize(); // Call initial check
});

// Handle resize for dynamic layout adjustment
function handleLayoutOnResize() {
    if (window.innerWidth > 768) {
        // Desktop/Tablet view: sidebar always open, content shifted
        sidebar.classList.remove('open'); // Ensure mobile class is off
        mainContent.classList.remove('shifted'); // Ensure mobile class is off
        mainContainer.style.marginLeft = 'var(--sidebar-width-desktop)'; // Use desktop variable for margin
        sidebar.style.left = '0'; // Ensure sidebar is visible
        // Adjust header width/position for desktop
        document.querySelector('header').style.left = 'var(--sidebar-width-desktop)';
        document.querySelector('header').style.width = 'calc(100% - var(--sidebar-width-desktop))';
    } else {
        // Mobile view: sidebar hidden by default, no initial push
        sidebar.classList.remove('open');
        mainContent.classList.remove('shifted');
        mainContainer.style.marginLeft = '0'; // No margin for mobile container
        sidebar.style.left = '-100vw'; // Ensure sidebar is off-screen initially
        // Adjust header width/position for mobile
        document.querySelector('header').style.left = '0';
        document.querySelector('header').style.width = '100%';
    }
}

window.addEventListener('resize', handleLayoutOnResize); // Attach event listener